function risk_level_color(r) {
	switch (r) {
		case 0: return '';
		case 1: return '#9dff9c';
		case 2: return '#f5ff9c';
		case 3: return '#ffd29c';
		case 4: return '#ff9c9c';
	}
}
function risk_level_name(r) {
	switch (r) {
		case 0: return '';
		case 1: return 'ต่ำ';
		case 2: return 'ปานกลาง';
		case 3: return 'สูง';
		case 4: return 'สูงมาก';
	}
}
function risk_level_acceptable(r) {	if  (r>=3) 		return 'ไม่เพียงพอ';	else		return 'เพียงพอ';
}
function risk_level_acceptable_color(r) {	if  (r>=3) 		return '#ff9c9c';	else		return '#9dff9c';}

function cal_level(w) {
	var i = parseInt($('#csa_impact_id'+w).val())-1;
	var j = parseInt($('#csa_likelihood_id'+w).val())-1;		if (isNaN(i) || isNaN(j)) {		$('#risk_level_'+w+'_div').css('background-color', '#ffffff');		$('#risk_level_'+w+'_div').html('');		$('#risk_level_'+w+'_txt').val(0);		$('#risk_level_'+w+'_1_div').html('');		$('#risk_level_'+w+'_1_div').css('background-color', '#ffffff');	} else {		lv = risk_mat[i][j];
		$('#risk_level_'+w+'_div').css('background-color', risk_level_color(lv));
		$('#risk_level_'+w+'_div').html(risk_level_name(lv));
		$('#risk_level_'+w+'_txt').val(lv);		$('#risk_level_'+w+'_1_div').html(risk_level_acceptable(lv));		$('#risk_level_'+w+'_1_div').css('background-color', risk_level_acceptable_color(lv));	}}
